self.onDataUpdated = function() {
    settings = self.ctx.settings;
        var data = [];
        var datasourceData = self.ctx.data[0];
        var dataSet = datasourceData.data;
        let currentHourCalculate = 0;
        let currentHourValue = 0;
        for (var d = 0; d < dataSet.length; d++) {

            var tsValuePair = dataSet[d];
            var ts = tsValuePair[0];
            var value = tsValuePair[1];

            var dt=new Date(ts);

            var day = dt.getDay();

            var hour = dt.getHours();
            console.log (hour);
            if (hour != currentHourCalculate)
            {
                if (settings.yAxisType == 'Monday to Friday')
                {

                    day = day - 1;
                }
                data.push([day, currentHourCalculate, currentHourValue]);

                currentHourValue = 0;
            }
            currentHourValue = currentHourValue + value;

            currentHourCalculate = hour;
        }
        console.log (data);
        draw (data);


}

