descriptor.controllerScript:
var seriesMarkLine = {}
var myChart;
var option;
var serverAttributes = [];
var clientAttributes = [];

self.onInit = function() {
    var settings = self.ctx.settings;
    if (self.ctx.settings.annotations !== undefined) {
        var clientAttributesToPoll = []
        var serverAttributesToPoll = []
        var clientAttributesCounter = 0;
        var serverAttributesCounter = 0;

        for (var i = 0; i < self.ctx.settings
            .annotations.length; i++) {
            if ((self.ctx.settings.annotations[i]
                    .attributeType == 'Client') && (self
                    .ctx.settings.annotations[i]
                    .annotationsType == 'Attribute')) {
                if (clientAttributesCounter > 0)
                    clientAttributesToPoll =
                    clientAttributesToPoll + ','
                clientAttributesToPoll.push(self.ctx
                    .settings.annotations[i]
                    .attribute);
                clientAttributesCounter++;
            }
            if ((self.ctx.settings.annotations[i]
                    .attributeType == 'Server') && (self
                    .ctx.settings.annotations[i]
                    .annotationsType == 'Attribute')) {


                if (serverAttributesCounter > 0)
                    serverAttributesToPoll =
                    serverAttributesToPoll + ','
                serverAttributesToPoll.push(self.ctx
                    .settings.annotations[i]
                    .attribute);
                serverAttributesCounter++;
            }

        }
    }






    if (clientAttributesToPoll !== '') {
        var entityId = self.ctx.datasources[0].entityId;
        var entity = {
            entityType: 'DEVICE',
            id: entityId
        }

        self.ctx.attributeService.getEntityAttributes(
            entity, 'CLIENT_SCOPE',
            clientAttributesToPoll
        ).subscribe((attributes) => {

            //Add Annotations that has been defined as ClientAttributes
            annotiatons = [];
            for (var i = 0; i < attributes
                .length; i++) {{


                for (var j = 0; j < self.ctx
                    .settings.annotations
                    .length; j++) {
                    if (self.ctx.settings
                        .annotations[j]
                        .attribute ==
                        attributes[i].key) {



                        clientAttributes.push(
                            {
                                'key': attributes[i].key,
                                'value': parseFloat(attributes[i].value),
                                'color': self.ctx
                            .settings
                            .annotations[j]
                            .color,
                            'axisAssignment': self.ctx.settings.annotations[j].axisAssignment
                            });





                    }

                }
                }
                }



        })
    }




    if (serverAttributesToPoll !== '') {

        var entityId = self.ctx.datasources[0].entityId;
        var entity = {
            entityType: 'DEVICE',
            id: entityId
        }

        self.ctx.attributeService.getEntityAttributes(
            entity, 'SERVER_SCOPE',
            serverAttributesToPoll
        ).subscribe((attributes) => {

            //Add Annotations that has been defined as ClientAttributes
            annotiatons = [];

            for (var i = 0; i < attributes
                .length; i++) {


                for (var j = 0; j < self.ctx
                    .settings.annotations
                    .length; j++) {
                    if (self.ctx.settings
                        .annotations[j]
                        .attribute ==
                        attributes[i].key) {



                        serverAttributes.push(
                            {
                                'key': attributes[i].key,
                                'value': parseFloat(attributes[i].value),
                                'color': self.ctx
                            .settings
                            .annotations[j]
                            .color,
                            'axisAssignment': self.ctx.settings.annotations[j].axisAssignment
                            });





                    }

                }
            }

        })
    }



    self.onResize();


}

    function adjustHexOpacity(color, opacity) {

        const r = parseInt(color.slice(1, 3), 16);
        const g = parseInt(color.slice(3, 5), 16);
        const b = parseInt(color.slice(5, 7), 16);

        return 'rgba(' + r + ', ' + g + ', ' + b + ', ' +
            opacity + ')';
    }

