descriptor.controllerScript:
self.onInit = function() {
    container = self.ctx.$container;
    var scope = self.ctx.$scope;
    var settings = self.ctx.settings;
    var sliderColor = settings.sliderBackgroundColor || tinycolor('green').lighten(2).toRgbString();
    var orientation = 'horizontal';
    if (settings.orientation == "1")
    {
        orientation = 'vertical';
        $('.slider', self.ctx.$container)[0].style.height = (parseInt((self.ctx.height), 10)-15)+'px' ;
        $('.slider', self.ctx.$container)[0].style.marginLeft = (parseInt((self.ctx.width), 10) / 2 -30) + 'px';
        $('.slider', self.ctx.$container)[0].style.marginTop = '-20px';
    }
    else
    {
        $('.slider', self.ctx.$container)[0].style.marginLeft = '0px';
        //Align Slider in to the middle of the container

        $('.slider', self.ctx.$container)[0].style.marginTop = (parseInt((self.ctx.height), 10) / 2 -30) + 'px';
    }




    var slider = $('.slider', self.ctx.$container);
    noUiSlider.create(slider[0], {
	    start: [settings.sliderInitMinValue || 20, settings.sliderInitMaxValue || 25],
	    tooltips: [true,true],
	    range: { min: settings.sliderMinValue || 0, max: settings.sliderMaxValue || 100 },
	    orientation: orientation,
        step: settings.sliderStepWidth || 1,
        connect: true
    });

    slider[0].noUiSlider.on('change.one', sliderValueChanged);

    function sliderValueChanged(values, handle, unencoded, tap, positions, noUiSlider) 
    {
        var method = settings.sliderMaxChangeRequestMethod;

        //Get the Handle which has been used
        if (handle === 0)
            method = settings.sliderMinChangeRequestMethod;
        var paramsBody = values[handle];



        self.ctx.controlApi.sendTwoWayCommand(method, 
                                    paramsBody, scope.requestTimeout)
                    .subscribe(
                        function success(responseBody) {

                        }
                    );
    }



    $('.noUi-connect', self.ctx.$container)[0].style.background = sliderColor;

    if (!settings.sliderHideTooltip)
    {
        document.getElementsByClassName('noUi-tooltip')[0].style.display = "block";
        document.getElementsByClassName('noUi-tooltip')[1].style.display = "block";
    }

    function requestMaxStatus() {
        method = settings.sliderMaxStatusRequestMethod;
        paramsBody = "";
        self.ctx.controlApi.sendTwoWayCommand(method, 
                            paramsBody, 
                            scope.requestTimeout)
            .subscribe(
                function success(responseBody) {
                       slider[0].noUiSlider.set( [null,parseFloat(responseBody)]);            
                    }
                    );
                }

    function requestMinStatus() {
        method = settings.sliderMinStatusRequestMethod;
        paramsBody = "";
        self.ctx.controlApi.sendTwoWayCommand(method, 
                            paramsBody, 
                            scope.requestTimeout)
            .subscribe(
                function success(responseBody) {
                       slider[0].noUiSlider.set([parseFloat(responseBody), null]);

                    }
                    )
                }


    requestMaxStatus()
    requestMinStatus()
}




self.onResize = function() {
    var settings = self.ctx.settings;
    var orientation = 'horizontal';
    if (settings.orientation == "1")
    {
        orientation = 'vertical';
        $('.slider', self.ctx.$container)[0].style.height = (parseInt((self.ctx.height), 10)-15)+'px' ;
        $('.slider', self.ctx.$container)[0].style.marginLeft = (parseInt((self.ctx.width), 10) / 2 -30) + 'px';
        $('.slider', self.ctx.$container)[0].style.marginTop = '-20px';
    }
    else
    {
        $('.slider', self.ctx.$container)[0].style.marginLeft = '0px';
        //Align Slider in to the middle of the container

        $('.slider', self.ctx.$container)[0].style.marginTop = (parseInt((self.ctx.height), 10) / 2 -30) + 'px';
    }
}

self.onDestroy = function() {
}

