     if (dataSet.length > 0)
     {
            var tsValuePair = dataSet[0];
            if (tsValuePair.length > 0)
            {
            var value = tsValuePair[1];
            if (value > settings.maxValue)
                value = settings.maxValue;
            if (value < settings.minValue)
                value = settings.minValue;

            var span =     (settings.maxValue - settings.minValue );
            var multiplier = 220 / span;

            seriesElement = [(value - settings.minValue) * multiplier  , 220-((value -settings.minValue) * multiplier)];
            //chart.container.innerText = value;
            $('#value', self.ctx.$container)[0].innerHTML = Number.parseFloat(value).toFixed(2) + ' ' + settings.unit;
            chart.series = seriesElement;
            chart.update(chart);
        }
}




}

