    if (self.ctx.width > (self.ctx.height - 50))
    {
        $('.ct-gauge', self.ctx.$container)[0].style.zoom = self.ctx.height / 200;
    }
    else
    {
        $('.ct-gauge', self.ctx.$container)[0].style.zoom = self.ctx.width / 200;
    }

}

