self.onInit = function() {
    settings = self.ctx.settings;


    var opts = {
    angle: settings.angle/100 || 0, // The span of the gauge arc
  lineWidth: settings.width || 0.4, // The line thickness
  radiusScale: 1, // Relative radius
  pointer: {
    length: 0.6, // // Relative to gauge radius
    strokeWidth: 0.035, // The thickness
    color: '#000000' // Fill color
  },
  limitMax: false,     // If false, max value increases automatically if value > maxValue
  limitMin: false,     // If true, the min value of the gauge will be fixed
  colorStart: '#6FADCF',   // Colors
  colorStop: '#8FC0DA',    // just experiment with them
  strokeColor: '#E0E0E0',  // to see which ones work best for you
  generateGradient: true,
  highDpiSupport: true,     // High resolution support
  percentColors: [[0.0, settings.startColor || "#a9d70b" ],  [1.0, settings.endColor || "#ff0000"]],

};

    canvasElement = $('#my-gauge', self.ctx.$container)[0];
    gauge = new Gauge(canvasElement).setOptions(opts);;
    gauge.minValue = settings.minValue||0; 
    gauge.maxValue = settings.maxValue||100; 
    gauge.animationSpeed = 16; 
    self.onResize();
}

self.onResize = function() {

    if (self.ctx.width < self.ctx.height+50)
    $('#preview', self.ctx.$container)[0].style.zoom = self.ctx.width/290;
    else
    {
    $('#preview', self.ctx.$container)[0].style.zoom = self.ctx.height/200;
    $('#preview-textfield', self.ctx.$container)[0].style.width = $('#preview', self.ctx.$container)[0].style.width;
    }




    //canvasElement.width = self.ctx.width;
    //canvasElement.height = self.ctx.height;
    gauge.update(true);
    gauge.render();
}

self.onDataUpdated = function() {
    if (self.ctx.data.length > 0)
    {
        if (self.ctx.data[0].data.length > 0)
        {
    var value = self.ctx.data[0].data[0][1];
    gauge.set(value);
    $('#preview-textfield', self.ctx.$container)[0].innerText = value.toFixed(2) + ' ' + (settings.unit || 'C');
        }
    }
}

