var namespace;
var cssParser = new cssjs();

var style = document.querySelector('[data="test"]');

self.onInit = function() {




    var settings = self.ctx.settings;

    var slider = $('#myRange', self.ctx.$container)[0]

    if (settings.style == 'Round')
        $('#myRange', self.ctx.$container).removeClass('slider').addClass('slider_round');


    slider.min = settings.sliderMinValue;
    slider.max = settings.sliderMaxValue;
    slider.step = settings.sliderStepWidth;
   slider.style.background = settings.sliderbgColor;

    function requestStatus() {
    method = settings.sliderStatusRequestMethod;
    paramsBody = "";
    self.ctx.controlApi.sendTwoWayCommand(method, 
                        paramsBody, 
                        settings.requestTimeout)
        .subscribe(
            function success(responseBody) {
                   slider.value = parseFloat(responseBody);    
                   self.onResize();
                }
                );
            }

    requestStatus();
    self.onResize();

    slider.oninput = function() {
        let sliderWidth = $('#myRange', self.ctx.$container)[0].offsetWidth;

        let margin = self.ctx.width - sliderWidth;
        $('#value_label', self.ctx.$container)[0].style.left = (sliderWidth) * slider.value/settings.sliderMaxValue + margin/2 - 20 + "px";
        if (settings.showValue)
            $('#value_label', self.ctx.$container)[0].innerHTML = slider.value;
        else
            $('#value_label', self.ctx.$container)[0].innerHTML = '';



    }

    slider.onchange = function() {
        var method = settings.sliderChangeRequestMethod;

        var paramsBody = slider.value;



        self.ctx.controlApi.sendTwoWayCommand(method, 
                                    paramsBody, settings.requestTimeout)
                    .subscribe(
                        function success(responseBody) {

                        }
                    );
}

}



self.onResize = function() {
    var slider = $('#myRange', self.ctx.$container)[0]
    var settings = self.ctx.settings;

    $('#myRange', self.ctx.$container)[0].style.marginTop = self.ctx.height / 2 - $('#myRange', self.ctx.$container)[0].offsetHeight / 2 -  $('#value_label', self.ctx.$container)[0].offsetHeight/2 + "px";

    $('#value_label', self.ctx.$container)[0].style.top = self.ctx.height / 2 - $('#myRange', self.ctx.$container)[0].offsetHeight -  $('#value_label', self.ctx.$container)[0].offsetHeight + "px";

    let sliderWidth = $('#myRange', self.ctx.$container)[0].offsetWidth;
    let margin = self.ctx.width - sliderWidth;

    $('#value_label', self.ctx.$container)[0].style.left = (sliderWidth) * slider.value/settings.sliderMaxValue + margin/2 - 20 + "px";
    if (settings.showValue)
    $('#value_label', self.ctx.$container)[0].innerHTML = slider.value;
    else
            $('#value_label', self.ctx.$container)[0].innerHTML = '';
}

self.onDestroy = function() {
}

