descriptor.templateCss:
#changeDashboardState {

width: 100%;
    top: 50%;
    text-align: center;
    position: absolute;
}

#dashboardState
{
    margin-left: 5px;
}

descriptor.controllerScript:
self.onInit = function() {

    var settings = self.ctx.settings;
    //set Font size of select
    $('#dashboardState', self.ctx.$container)[0].style.fontSize = settings.fontSize + "px";
    //set Font size of label
    $('#dashboardStateLabel', self.ctx.$container)[0].style.fontSize = settings.fontSize + "px";

    //set Font color of Select
    $('#dashboardState', self.ctx.$container)[0].style.color = settings.fontColor;
    //set Font color of Label
    $('#dashboardStateLabel', self.ctx.$container)[0].style.color = settings.fontColor;

    //set Label Text according to settings
    $('#dashboardStateLabel', self.ctx.$container)[0].innerHTML = settings.labelText;


    //Add Options to the Select
    for (var i = 0; i < settings.dashboardStateList.length; i++)
    {
        $('#dashboardState', self.ctx.$container).append($('<option>', {
                    text: settings.dashboardStateList[i].listEntry
                }));
    }


    //Select changed
    $('#dashboardState', self.ctx.$container).change(function() {
    var listEntry = ('#dashboardState', self.ctx.$container).find(":selected").text()
    console.log("list Entry:" + listEntry)

    //Get Alias for the given name
    var alias = settings.dashboardStateList[0].dashboardAlias;
    for (var i = 0; i < settings.dashboardStateList.length; i++)
    {
        if (settings.dashboardStateList[i].listEntry == listEntry)
        {
            alias = settings.dashboardStateList[i].dashboardAlias;
            break;
        }
    }

  var selection = alias;

  var stateController = self.ctx.stateController;
  var stateID = stateController.getStateId();
  var params = stateController.getStateParams();

  var entityId = (getEntityID(selection));

  params.entityName = selection;
  params.entityId.id = entityId;


stateController.resetState();
  stateController.updateState(stateID=stateID, params=params, true);


});


function getEntityID (alias)
{
    var aliases = self.ctx.dashboard.aliasController.entityAliases;
   for (let element in aliases)
   {

        if (aliases[element].alias == alias)
            return aliases[element].filter.singleEntity.id;
   }

}


}

self.actionSources = function() {
    return {
        'elementClick': {
            name: 'widget-action.element-click',
            multiple: true
        }
    };
}

self.onDestroy = function() {
}

descriptor.settingsSchema:
{
    "schema": {
        "type": "object",
        "title": "Settings",
        "properties": {

            "fontSize": {
                "title": "Font size",
                "type": "number",
                "default": 16
            },
            "fontColor": {
                "title": "Font color",
                "type": "string",
                "default": "#008a00"
            },
            "labelText": {
                "title": "Label Text",
                "type": "string",
                "default": "Change Dashboard State"
            },
            "dashboardStateList": {
                "title": "Dashboard State list",
                "type": "array",
                "minItems" : 1,
                "items": {
                    "title": "Dashboard State",
                    "type": "object",
                    "properties": {
                        "dashboardAlias": {
                            "title": "Dashboard Alias",
                            "type": "string"
                        },
                        "listEntry": {
                            "title": "List Entry",
                            "type": "string"
                        }
                    },
                    "required": ["dashboardAlias", "listEntry"]
                }
            }
            },
        "required": ["dashboardStateList"]
    },
    "form": [
        "fontSize",
        "fontColor",
        "labelText",
        "dashboardStateList"
    ]
}

