descriptor.templateCss:
#container {
  padding: 20px;
}

.slider {
    margin-right: 40px
}

.noUi-tooltip {
    display: none;
}
.noUi-active .noUi-tooltip {
    display: block;
}

.noUi-connect {
	background: red;
}

descriptor.controllerScript:
self.onInit = function() {
    container = self.ctx.$container;
    var scope = self.ctx.$scope;
    var settings = self.ctx.settings;
    var sliderColor = settings.sliderBackgroundColor || tinycolor('green').lighten(2).toRgbString();
    var orientation = 'horizontal';
    if (settings.orientation == "1")
    {
        orientation = 'vertical';
        $('.slider', self.ctx.$container)[0].style.height = (parseInt((self.ctx.height), 10)-15)+'px' ;
        $('.slider', self.ctx.$container)[0].style.marginLeft = (parseInt((self.ctx.width), 10) / 2 -30) + 'px';
        $('.slider', self.ctx.$container)[0].style.marginTop = '-20px';
    }
    else
    {
        $('.slider', self.ctx.$container)[0].style.marginLeft = '0px';
        //Align Slider in to the middle of the container

        $('.slider', self.ctx.$container)[0].style.marginTop = (parseInt((self.ctx.height), 10) / 2 -30) + 'px';
    }




    var slider = $('.slider', self.ctx.$container);
    noUiSlider.create(slider[0], {
	    start: [settings.sliderInitMinValue || 20, settings.sliderInitMaxValue || 25],
	    tooltips: [true,true],
	    range: { min: settings.sliderMinValue || 0, max: settings.sliderMaxValue || 100 },
	    orientation: orientation,
        step: settings.sliderStepWidth || 1,
        connect: true
    });

    slider[0].noUiSlider.on('change.one', sliderValueChanged);

    function sliderValueChanged(values, handle, unencoded, tap, positions, noUiSlider) 
    {
        var method = settings.sliderMaxChangeRequestMethod;

        //Get the Handle which has been used
        if (handle === 0)
            method = settings.sliderMinChangeRequestMethod;
        var paramsBody = values[handle];



        self.ctx.controlApi.sendTwoWayCommand(method, 
                                    paramsBody, scope.requestTimeout)
                    .subscribe(
                        function success(responseBody) {

                        }
                    );
    }



    $('.noUi-connect', self.ctx.$container)[0].style.background = sliderColor;

    if (!settings.sliderHideTooltip)
    {
        document.getElementsByClassName('noUi-tooltip')[0].style.display = "block";
        document.getElementsByClassName('noUi-tooltip')[1].style.display = "block";
    }

    function requestMaxStatus() {
        method = settings.sliderMaxStatusRequestMethod;
        paramsBody = "";
        self.ctx.controlApi.sendTwoWayCommand(method, 
                            paramsBody, 
                            scope.requestTimeout)
            .subscribe(
                function success(responseBody) {
                       slider[0].noUiSlider.set( [null,parseFloat(responseBody)]);            
                    }
                    );
                }

    function requestMinStatus() {
        method = settings.sliderMinStatusRequestMethod;
        paramsBody = "";
        self.ctx.controlApi.sendTwoWayCommand(method, 
                            paramsBody, 
                            scope.requestTimeout)
            .subscribe(
                function success(responseBody) {
                       slider[0].noUiSlider.set([parseFloat(responseBody), null]);

                    }
                    )
                }


    requestMaxStatus()
    requestMinStatus()
}




self.onResize = function() {
    var settings = self.ctx.settings;
    var orientation = 'horizontal';
    if (settings.orientation == "1")
    {
        orientation = 'vertical';
        $('.slider', self.ctx.$container)[0].style.height = (parseInt((self.ctx.height), 10)-15)+'px' ;
        $('.slider', self.ctx.$container)[0].style.marginLeft = (parseInt((self.ctx.width), 10) / 2 -30) + 'px';
        $('.slider', self.ctx.$container)[0].style.marginTop = '-20px';
    }
    else
    {
        $('.slider', self.ctx.$container)[0].style.marginLeft = '0px';
        //Align Slider in to the middle of the container

        $('.slider', self.ctx.$container)[0].style.marginTop = (parseInt((self.ctx.height), 10) / 2 -30) + 'px';
    }
}

self.onDestroy = function() {
}

descriptor.settingsSchema:
{
    "schema": {
        "type": "object",
        "title": "Settings",
        "properties": {

            "requestTimeout": {
                "title": "RPC request timeout",
                "type": "number",
                "default": 500
            },
            "sliderBackgroundColor": {
                "title": "Slider background color",
                "type": "string",
                "default": "#008a00"
            },
            "sliderHideTooltip": {
                "title": "Slider hide tooltip",
                "type": "boolean",
                "default": true
            },
            "sliderStepWidth": {
                "title": "Slider step width",
                "type": "number",
                "default": 1
            },
            "sliderMinValue": {
                "title": "Slider min. Value",
                "type": "number",
                "default": 1
            },
            "sliderMaxValue": {
                "title": "Slider max. Value",
                "type": "number",
                "default": 100
            },
            "sliderInitMinValue": {
                "title": "Slider initialization min. Value",
                "type": "number",
                "default": 20
            },
            "sliderInitMaxValue": {
                "title": "Slider initialization max. Value",
                "type": "number",
                "default": 25
            },
            "orientation": {
                "title": "Orientation",
                "type": "string",
                "enum": [
                    "0",
                    "1"],
                "default": "0"
            },
            "sliderMaxChangeRequestMethod": {
                "title": "Change request max. Value Method",
                "type": "string",
                "default": "setTempertureOFFThreshold"
            },
            "sliderMaxStatusRequestMethod": {
                "title": "Status request max. Value Method",
                "type": "string",
                "default": "getTempertureOFFThreshold"
            },
            "sliderMinChangeRequestMethod": {
                "title": "Change request min. Value Method",
                "type": "string",
                "default": "setTempertureONThreshold"
            },
            "sliderMinStatusRequestMethod": {
                "title": "Status request min. Value Method",
                "type": "string",
                "default": "getTempertureONThreshold"
            }
        },
        "required": [
                     "requestTimeout",
                     "sliderHideTooltip",
                     "sliderMinValue",
                     "sliderMaxValue",
                     "sliderInitMinValue",
                     "sliderMaxValue",
                     "sliderStepWidth",
                     "sliderBackgroundColor",
                     "orientation",
                     "sliderMaxChangeRequestMethod",
                     "sliderMaxStatusRequestMethod",
                     "sliderMinChangeRequestMethod",
                     "sliderMinStatusRequestMethod"
                     ]
    },
    "form": [
        "requestTimeout",
        "sliderHideTooltip",
        "sliderMinValue",
        "sliderMaxValue",
        "sliderInitMinValue",
        "sliderInitMaxValue",
        "sliderStepWidth",
        {
            "key": "sliderBackgroundColor",
            "type": "color"
        },
        {
            "key": "orientation",
            "type": "select",
            "titleMap": [{
                "value": "0",
                "name": "Horizontal"
            },
            {
                "value": "1",
                "name": "Vertical"
            }]
        },
        "sliderMaxChangeRequestMethod",
         "sliderMaxStatusRequestMethod",
         "sliderMinChangeRequestMethod",
         "sliderMinStatusRequestMethod"
    ]
}
