}

descriptor.controllerScript:
var data;
var options;
var chart;
self.onInit = function() {
    var settings = self.ctx.settings;
      google.charts.load('current', {'packages':['gauge']});
      google.charts.setOnLoadCallback(drawChart);

      function drawChart() {

        data = google.visualization.arrayToDataTable([
          ['Label', 'Value'],
          [settings.description || "Temp.", 0]
        ]);

        options = {
          width: self.ctx.width, height: self.ctx.height,
          min: settings.minValue || 0, max: settings.maxValue,
          greenFrom: settings.greenStart, greenTo: settings.greenEnd,
          redFrom: settings.redStart, redTo: settings.redEnd,
          yellowFrom:settings.yellowStart, yellowTo: settings.yellowEnd,
          minorTicks: 5
        };

        chart = new google.visualization.Gauge($('#chart_div', self.ctx.$container)[0]);

        chart.draw(data, options);

        self.onDataUpdated();


      }
}

self.onDataUpdated = function() {
    var settings = self.ctx.settings;
    var value = 0;
    console.log (self.ctx);

    if (self.ctx.data.length > 0)
    {

        if (self.ctx.data[0].data.length > 0)
        value = self.ctx.data[0].data[0][1];
        console.log (value);


        }


        if (data !=undefined)
        {
            console.log (value);
            data.setValue(0, 1, value);
            chart.draw(data, options);
        }
}

self.onResize = function() {
    //if (options != undefined)
    //{
