    //options.height = self.ctx.height;
    //}
    //$('#chart_div', self.ctx.$container)[0].style.marginLeft = -200 + self.ctx.width/2 + "px";


    //if (chart != undefined)
   //chart.draw(data, options);
   self.onInit();
}

self.onDestroy = function() {
}

descriptor.settingsSchema:
{
    "schema": {
        "type": "object",
        "title": "Settings",
        "properties": {
            "description": {
                "title": "Description",
                "type": "string",
                "default": ""
            },
            "maxValue": {
                "title": "Maximum Value",
                "type": "number",
                "default": 100
            },
            "minValue": {
                "title": "Minimum Value",
                "type": "number",
                "default": 0
            },
            "greenStart": {
                "title": "Start green area",
                "type": "number",
                "default": 0
            },
            "greenEnd": {
                "title": "End green area",
                "type": "number",
                "default": 0
            },
            "yellowStart": {
                "title": "Start Yellow area",
                "type": "number",
                "default": 0
            },
            "yellowEnd": {
                "title": "End Yellow area",
                "type": "number",
                "default": 0
            },
            "redStart": {
                "title": "Start Red area",
                "type": "number",
                "default": 0
            },
            "redEnd": {
                "title": "End Red area",
                "type": "number",
                "default": 0
            }

        },
        "required": ["maxValue"]
    },
    "form": [
        "description",
        "maxValue",
        "minValue",
        "greenStart",
        "greenEnd",
        "yellowStart",
        "yellowEnd",
        "redStart",
        "redEnd"
            ]
}
