descriptor.controllerScript:
var gaugeData = [];
self.onInit = function() {




for (var i = 0; i<self.ctx.data.length; i++)
{

    let gaugeDataElement = {


    name: self.ctx.data[i].dataKey.label,
    title: {
      offsetCenter: [0+'%', -30+i*30+'%']
    },
    detail: {
      valueAnimation: true,
      offsetCenter: [0+'%', -20+i*30+'%']
    }
  }


    gaugeData.push(gaugeDataElement);
}


    draw();
    self.onResize();
}
var gaugeData = [];
function draw()
{

var myChart = echarts.init($('#echart', self.ctx.$container)[0]);


var option;





    /*var gaugeData = [
  {
    value: 20,
    name: 'Perfect',
    title: {
      offsetCenter: ['0%', '-30%']
    },
    detail: {
      valueAnimation: true,
      offsetCenter: ['0%', '-20%']
    }
  },
  {
    value: 40,
    name: 'Good',
    title: {
      offsetCenter: ['0%', '0%']
    },
    detail: {
      valueAnimation: true,
      offsetCenter: ['0%', '10%']
    }
  },
  {
    value: 60,
    name: 'Commonly',
    title: {
      offsetCenter: ['0%', '30%']
    },
    detail: {
      valueAnimation: true,
      offsetCenter: ['0%', '40%']
    }
  }
];*/
option = {
  series: [
    {
      type: 'gauge',
      startAngle: 90,
      endAngle: -270,
      min: self.ctx.settings.minValue || 0,
      max: self.ctx.settings.maxValue || 100,
      pointer: {
        show: false
      },
      progress: {
        show: true,
        overlap: false,
        roundCap: true,
        clip: false,
        itemStyle: {
          borderWidth: 1,
          borderColor: '#464646'
        }
      },
      axisLine: {
        lineStyle: {
          width: 40
        }
      },
      splitLine: {
        show: false,
        distance: 0,
        length: 10
      },
      axisTick: {
        show: false
      },
      axisLabel: {
        show: false,
        distance: 50
      },
      data: gaugeData,
      title: {
        fontSize: 14
      },
      detail: {
        width: 50,
        height: 14,
        fontSize: 14,
        color: 'auto',
        borderColor: 'auto',
        borderRadius: 20,
        borderWidth: 1,
        formatter: '{value}' + ((self.ctx.settings.unitValue === undefined) ? '': self.ctx.settings.unitValue)
      }
    },
  ]
};
/*setInterval(function () {
  gaugeData[0].value = +(Math.random() * 100).toFixed(2);
  gaugeData[1].value = +(Math.random() * 100).toFixed(2);
  gaugeData[2].value = +(Math.random() * 100).toFixed(2);
  myChart.setOption({
    series: [
      {
        data: gaugeData,
        pointer: {
          show: false
        }
      }
    ]
  });
}, 2000);*/
option && myChart.setOption(option);
}

self.onDataUpdated = function() {
   console.log(gaugeData);
    for (var i = 0; i<self.ctx.data.length; i++)
{
    if (self.ctx.data[i].data[0] != undefined)
    gaugeData[i].value = (self.ctx.data[i].data[0][1])

}

draw();


}

self.onResize = function() {
    if (self.ctx.width > self.ctx.height)
        $('#echart', self.ctx.$container)[0].style.zoom = self.ctx.height/500;
    else
    $('#echart', self.ctx.$container)[0].style.zoom = self.ctx.width/500;

    self.ctx.detectChanges();

}

self.onDestroy = function() {
}

