descriptor.controllerScript:
//https://www.cssscript.com/demo/circular-range-slider-svg/
//https://github.com/tadejf84/js-range-slider
//https://webcodeflow.com/concentric-circular-range-slider/
var slider;
var opts;
var values = [0,0,0,0];
self.onInit = function() {
    //var settings = self.ctx.settings;
    //    var sliders = [];
    //    console.log(settings);
    //    settings.sliderList.forEach(function(value){
    //         var sliderelement = {
    //                radius: 100,
    //                min: 0,
    //                max: 100,
    //                step: 10,
    //                initialValue: 50,
    //                color: '#fdcb6e',
    //                displayName: 'Value 1'
    //            };

    //    });
       settings = self.ctx.settings;
    if (settings.sliderList != null)
    {

    for(var i = 0; i < settings.sliderList.length; i++)
        requestStatus(i);
    }



        self.onResize();




}

 function requestStatus(sliderNumber) {

        method = settings.sliderList[sliderNumber].rpcGetValueMethod;
        paramsBody = "";
        self.ctx.controlApi.sendTwoWayCommand(method, 
                            paramsBody, 
                            settings.requestTimeout)
            .subscribe(
                function success(responseBody) {

                        values[sliderNumber] = parseFloat(responseBody); 

                        self.onResize();  
                    }
                    );
                }



self.onResize = function() {
    settings = self.ctx.settings;

const context =  $('#app', self.ctx.$container).html("");

if (settings.sliderList != null)
{
    sliders = []
for(var i = 0; i < settings.sliderList.length; i++)
        {
            let color = 'red';
            if (settings.sliderList[i].color == 'Yellow')
                color = 'yellow';
            if (settings.sliderList[i].color == 'Blue')
                color = 'blue';
            if (settings.sliderList[i].color == 'Green')
                color = 'green';
            sliders.push({
                    radius: 40 + i*60,
                    min: settings.sliderList[i].minValue,
                    max: settings.sliderList[i].maxValue,
                    step: 1,
                    initialValue: values[i],
                    color: color,
                    displayName: settings.sliderList[i].name
                })


        }
        opts = {
            DOMselector: $('#app', self.ctx.$container)[0],
            sliders: sliders,
            scale: 1
        }
}
else
{

opts = {
            DOMselector: $('#app', self.ctx.$container)[0],

            sliders: [
                {
                    radius: 100,
                    min: 0,
                    max: 100,
                    step: 10,
                    initialValue: 50,
                    color: '#fdcb6e',
                    displayName: 'Value 1'
                },
                {
                    radius: 160,
                    min: 0,
                    max: 3000,
                    step: 100,
                    initialValue: 200,
                    color: '#0984e3',
                    displayName: 'Value 2'
                },
                {
                    radius: 40,
                    min: 0,
                    max: 200,
                    step: 10,
                    initialValue: 20,
                    color: '#d63031',
                    displayName: 'Value 3'
                }
            ], 
            scale: 1
        };
}

        var numberOfValues = 3;
        if (settings.sliderList != null)
            numberOfValues = settings.sliderList.length;



        if (self.ctx.width < self.ctx.height)
        {
            //opts.scale = 133*numberOfValues/self.ctx.width;
            opts.scale = 400/self.ctx.width;
        }
        else
        {
            //opts.scale = 133*numberOfValues/self.ctx.height;
            opts.scale = 400/self.ctx.width;
        }

        slider = new Slider(opts);
        slider.listener = function (handle, value) {valueUpdated(handle, value)}
        slider.draw();
}

function valueUpdated(handle, value)
{



    var handleNumber = handle;
    console.log (handleNumber);


    if (settings.sliderList != null)
    {
    var method = settings.sliderList[handleNumber].rpcSetValueMethod;
            var paramsBody = value;


            self.ctx.controlApi.sendOneWayCommand(method, 
                                        paramsBody, settings.requestTimeout)
                        /*.subscribe(
                            function success(responseBody) {

                            }
                        );*/
    }


}

self.onDestroy = function() {

}

descriptor.settingsSchema:
{
    "schema": {
        "type": "object",
        "title": "Settings",
        "properties": {
            "sliderList": {
                "title": "Slider List",
                "type": "array",
                "minItems" : 1,
                "items": {
                    "title": "Slider",
                    "type": "object",
                    "properties": {
                        "name": {
                            "title": "Name",
                            "type": "string"
                        },
                        "minValue": {
                            "title": "Min. value",
                            "type": "number",
                            "default": 0
                        },
                        "maxValue": {
                            "title": "Max. value",
                            "type": "number",
                            "default": 100
                        },
                        "rpcGetValueMethod": {
                        "title": "RPC get value method",
                        "type": "string",
                        "default": "getValue"
                    },
                        "rpcSetValueMethod": {
                        "title": "RPC set value method",
                        "type": "string",
                        "default": "setValue"
                    },
                        "color": {
                            "title": "Color",
                            "type": "string",
                            "default": "Red",
                             "enum": [
                                "Red",
                                "Yellow",
                                "Blue",
                                "Green"]
                        }
                    },
                    "required": ["name", "rpcGetValueMethod", "rpcSetValueMethod", "color"]
                }
            }
            },
        "required": ["sliderList"]
    },
    "form": [
        "sliderList"
    ]
}
