descriptor.templateCss:
#container {

  background-position: 50% 50%;

}

#value_label{
    position: absolute;
    top: 40px;
}

.slidecontainer {

 margin: auto;
  width: 90%; /* Width of the outside container */
  margin-left: 5%;

}

/* The slider itself */
.slider {
  -webkit-appearance: none;  /* Override default CSS styles */
  appearance: none;
  width: 100%; /* Full-width */
  height: 25px; /* Specified height */
  background: #d3d3d3; /* Grey background */
  outline: none; /* Remove outline */
  opacity: 0.7; /* Set transparency (for mouse-over effects on hover) */
  -webkit-transition: .2s; /* 0.2 seconds transition on hover */
  transition: opacity .2s;
}

/* Mouse-over effects */
.slider:hover {
  opacity: 1; /* Fully shown on mouse-over */
}

/* The slider handle (use -webkit- (Chrome, Opera, Safari, Edge) and -moz- (Firefox) to override default look) */ 
.slider::-webkit-slider-thumb {
  -webkit-appearance: none; /* Override default look */
  appearance: none;
  width: 25px; /* Set a specific slider handle width */
  height: 25px; /* Slider handle height */
  background: blue; /* Green background */
  cursor: pointer; /* Cursor on hover */
}

.slider::-moz-range-thumb {
  width: 25px; /* Set a specific slider handle width */
  height: 25px; /* Slider handle height */
  background: blue; /* Green background */
  cursor: pointer; /* Cursor on hover */
}

descriptor.controllerScript:
var namespace;
var cssParser = new cssjs();

self.onInit = function() {
    var settings = self.ctx.settings;

    var slider = $('#myRange', self.ctx.$container)[0]
    slider.min = settings.sliderMinValue;
    slider.max = settings.sliderMaxValue;
   slider.style.background = settings.sliderbgColor;

    function requestStatus() {
    method = settings.sliderStatusRequestMethod;
    paramsBody = "";
    self.ctx.controlApi.sendTwoWayCommand(method, 
                        paramsBody, 
                        settings.requestTimeout)
        .subscribe(
            function success(responseBody) {
                   slider.value = parseFloat(responseBody);    
                   self.onResize();
                }
                );
            }

    requestStatus();
    self.onResize();

    slider.oninput = function() {
        let sliderWidth = $('#myRange', self.ctx.$container)[0].offsetWidth;

        let margin = self.ctx.width - sliderWidth;
        $('#value_label', self.ctx.$container)[0].style.left = (sliderWidth) * slider.value/settings.sliderMaxValue + margin/2 - 20 + "px";
        if (settings.showValue)
            $('#value_label', self.ctx.$container)[0].innerHTML = slider.value;
        else
            $('#value_label', self.ctx.$container)[0].innerHTML = '';



    }

    slider.onchange = function() {
        var method = settings.sliderChangeRequestMethod;

        var paramsBody = slider.value;



        self.ctx.controlApi.sendTwoWayCommand(method, 
                                    paramsBody, settings.requestTimeout)
                    .subscribe(
                        function success(responseBody) {

                        }
                    );
}

}



self.onResize = function() {
    var slider = $('#myRange', self.ctx.$container)[0]
    var settings = self.ctx.settings;

    $('#myRange', self.ctx.$container)[0].style.marginTop = self.ctx.height / 2 - $('#myRange', self.ctx.$container)[0].offsetHeight / 2 -  $('#value_label', self.ctx.$container)[0].offsetHeight/2 + "px";

    $('#value_label', self.ctx.$container)[0].style.top = self.ctx.height / 2 - $('#myRange', self.ctx.$container)[0].offsetHeight -  $('#value_label', self.ctx.$container)[0].offsetHeight + "px";

    let sliderWidth = $('#myRange', self.ctx.$container)[0].offsetWidth;
    let margin = self.ctx.width - sliderWidth;

    $('#value_label', self.ctx.$container)[0].style.left = (sliderWidth) * slider.value/settings.sliderMaxValue + margin/2 - 20 + "px";
    if (settings.showValue)
    $('#value_label', self.ctx.$container)[0].innerHTML = slider.value;
    else
            $('#value_label', self.ctx.$container)[0].innerHTML = '';
}

self.onDestroy = function() {
}

descriptor.settingsSchema:
{
    "schema": {
        "type": "object",
        "title": "Settings",
        "properties": {

            "requestTimeout": {
                "title": "RPC request timeout",
                "type": "number",
                "default": 500
            },
            "sliderbgColor": {
                "title": "Slider Background Color",
                "type": "string",
                "default": "#4CAF50"
            },
            "showValue": {
                "title": "Show Value",
                "type": "boolean",
                "default": true
            },
            "sliderMinValue": {
                "title": "Min. Value",
                "type": "number",
                "default": 1
            },
            "sliderMaxValue": {
                "title": "Max. Value",
                "type": "number",
                "default": 100
            },
            "sliderChangeRequestMethod": {
                "title": "Change request Method",
                "type": "string",
                "default": "setValue"
            },
            "sliderStatusRequestMethod": {
                "title": "Status request Method",
                "type": "string",
                "default": "getValue"
            }
        },
        "required": [
                     "requestTimeout",
                     "sliderbgColor",
                     "showValue",
                     "sliderMinValue",
                     "sliderMaxValue",
                     "sliderChangeRequestMethod",
                     "sliderStatusRequestMethod"
                     ]
    },
    "form": [
        "requestTimeout",
        {
            "key": "sliderbgColor",
            "type": "color"
        },
        "showValue",
        "sliderMinValue",
        "sliderMaxValue",
        "sliderChangeRequestMethod",
        "sliderStatusRequestMethod"
    ]
}
