descriptor.templateCss:
*, :after, :before {
	 box-sizing: border-box;
}
 body {
	 margin: 60px auto;
	 text-align: center;
	 background-color: #ff9295;
	 transition: background-color 0.2s cubic-bezier(0, -1.85, 0.27, 1.75);
}
 .toggle {
	 position: absolute;
	 top: 50%;
	 left: 50%;
	 transform: translate3d(-50%, -50%, 0);
}
 .toggle:before {

	 position: absolute;
	 left: -56px;
	 top: 30px;
	 font-size: 18px;
	 color: #fff;
}
 .toggle:after {

	 position: absolute;
	 right: -46px;
	 top: 30px;
	 font-size: 18px;
	 color: rgba(253, 15, 21, 0.31);
}
 .toggle label {
	 position: relative;
	 display: inline-block;
	 width: 80px;
	 height: 80px;
	 background-color: #fd1015;
	 border-radius: 50px;
	 cursor: pointer;
	 box-shadow: inset 0 0 2px 1px rgba(0, 0, 0, 0.1), 0px 9px 15px 0px #ef4247;
	 -webkit-tap-highlight-color: transparent;
}
 .toggle label:before {
	 content: "";
	 position: absolute;
	 top: 50%;
	 left: 50%;
	 transform: translate(-50%, -50%);
	 transition: width 0.2s cubic-bezier(0, -1.85, 0.27, 1.75);
	 height: 42px;
	 width: 42px;
	 background-color: #fd0f14;
	 border-radius: 46px;
	 box-shadow: inset 0px 0px 0px 8px #fff;
}
 .toggle input {
	 display: none;
}
 .toggle input:checked + label {
	 background-color: #57de72;
	 box-shadow: inset 0 0 2px 1px rgba(0, 0, 0, 0.1), 0px 9px 15px 0px rgba(3, 132, 28, 0.5411764705882353);
}
 .toggle input:checked + label:before {
	 width: 10px;
	 background-color: #fff;
}
 .on {
	 background-color: #6fc57c;
}
 .on .toggle:before {
	 color: rgba(62, 160, 81, 0.89);
}
 .on .toggle:after {
	 color: #fff;
}

descriptor.controllerScript:
//https://codepen.io/agoodwin/pen/JBvBPr
var namespace;
var cssParser = new cssjs();

self.onInit = function() {




    var settings = self.ctx.settings;

    self.onResize();


    $('#toggle', self.ctx.$container).change(
        function() {
            let checkbox_checked = false;
            if (this.checked) {
                checkbox_checked = true;
            }

            var method = settings
                .changeRequestMethod || 'setValue';
            var paramsBody = convertStatus(
                checkbox_checked);


            self.ctx.controlApi.sendOneWayCommand(
                method,
                paramsBody, settings
                .requestTimeout)


        });
    var convertStatusChangeMethod = settings
        .convertStatusChangeMethod || "return value;";

    var convertStatus = new Function("value",
        convertStatusChangeMethod);

    //Get Value Method
    var convertGetValueMethod = settings
        .convertStatusRequestMethod ||
        "return data ? true : false;";

    var convertGetValue = new Function("data",
        convertGetValueMethod);

    function requestStatus() {

        method = settings.statusRequestMethod;
        paramsBody = "";
        self.ctx.controlApi.sendTwoWayCommand(method,
                paramsBody,
                settings.requestTimeout)
            .subscribe(
                function success(responseBody) {

                    let value = (convertGetValue(
                        responseBody));

                    if (value) {
                        $('#toggle', self.ctx
                            .$container).prop(
                            'checked', true);



                    } else {
                        $('#toggle', self.ctx
                            .$container).prop(
                            'checked', false);
                    }
                }
            );
    }

    if ((settings.retrieveValueMethod ==
            "Subscribe for attribute") || (settings
            .retrieveValueMethod ==
            "Subscribe for timeseries")) {
        var type = 'timeseries'
        if (settings.retrieveValueMethod ==
            "Subscribe for attribute")
            type = 'attribute'


        datasources = [{ // datasource
                type: 'entity', // type of the datasource. Can be "function" or "entity"
                name: 'name', // name of the datasource (in case of "entity" usually Entity name)
                aliasName: self.ctx
                    .defaultSubscription
                    .targetDeviceName, // name of the alias used to resolve this particular datasource Entity
                entityName: self.ctx
                    .defaultSubscription
                    .targetDeviceName, // name of the Entity used as datasource
                entityType: 'DEVICE', // datasource Entity type (for ex. "DEVICE", "ASSET", "TENANT", etc.)
                entityId: self.ctx
                    .defaultSubscription
                    .targetDeviceId, // entity identificator presented as string uuid. 
                dataKeys: [ //  array of keys (Array<DataKey>) (attributes or timeseries) of the entity used to fetch data 
                    { // dataKey
                        name: settings
                            .updateValueKey, // the name of the particular entity attribute/timeseries 
                        type: type, // type of the dataKey. Can be "timeseries", "attribute" or "function" 
                        label: settings
                            .updateValueKey, // label of the dataKey. Used as display value (for ex. in the widget legend section) 
                        color: '#ffffff', // color of the key. Can be used by widget to set color of the key data (for ex. lines in line chart or segments in the pie chart).  
                        funcBody: "", // only applicable for datasource with type "function" and "function" key type. Defines body of the function to generate simulated data.
                        settings: {} // dataKey specific settings with structure according to the defined Data key settings json schema. See "Settings schema section".
                    },
                    //...
                ]
            },
            //...
        ]
        console.log(datasources);

        function subscribeForLabelPatternsSources(
            datasources) {
            const
                labelPatternsSourcesSubscriptionOptions = {
                    datasources,
                    useDashboardTimewindow: false,
                    type: 'latest',
                    callbacks: {
                        onDataUpdated: (
                        subscription) => {

                            let data = subscription
                                .data[0].data[0][1];

                            console.log(
                                subscription)

                            let value = (
                                convertGetValue(
                                    data));

                            if (value === true) {
                                console.log(
                                "check");
                                $('#toggle', self
                                        .ctx
                                        .$container)
                                    .prop('checked',
                                        true);

                            } else {
                                $('#toggle', self
                                        .ctx
                                        .$container)
                                    .prop('checked',
                                        false);
                                console.log(
                                    "uncheck");

                            }
                        }
                    }
                };

            self.ctx.subscriptionApi.createSubscription(
                labelPatternsSourcesSubscriptionOptions,
                true).subscribe(
                (subscription) => {
                    //this.labelPatternsSourcesSubscription = subscription;
                }
            );
        }
        subscribeForLabelPatternsSources(datasources);
    }

    if (settings.retrieveValueMethod ==
        'Call RPC get value method') {
        requestStatus()
    }
}



self.onResize = function() {


}

self.onDestroy = function() {
}

descriptor.settingsSchema:
{
    "schema": {
        "type": "object",
        "title": "Settings",
        "properties": {
            "retrieveValueMethod":
            {
                "title": "Retrieve on/off value using method",
                            "type": "string",
                            "enum": [
                                "Don't retrieve",
                                "Call RPC get value method",
                                "Subscribe for attribute",
                                "Subscribe for timeseries"],
                            "default": "Call RPC get value method"
            },
            "updateValueKey": {
                "title": "Attribute/Timeseries value key (only when subscribe for attribute/timeseries method)",
                "type": "string",
                "default": "value"
            },
            "requestTimeout": {
                "title": "RPC request timeout",
                "type": "number",
                "default": 500
            },
            "changeRequestMethod": {
                "title": "RPC set value method",
                "type": "string",
                "default": "setValue"
            },
            "statusRequestMethod": {
                "title": "RPC get value method",
                "type": "string",
                "default": "getValue"
            },
            "convertStatusChangeMethod": {
                "title": "Convert value function, f(value), returns payload used by RPC set value method",
                "type": "string",
                "default": "return value;"
            },
            "convertStatusRequestMethod": {
                "title": "Parse value function, f(data), returns boolean",
                "type": "string",
                "default": "return data ? true : false;"
            } 

        },
        "required": [ 
                    "retrieveValueMethod",
                     "statusRequestMethod",
                     "changeRequestMethod",
                     "convertStatusRequestMethod",
                     "convertStatusChangeMethod",
                     "requestTimeout"]
    },
    "form": [
        "retrieveValueMethod",
        "updateValueKey",
        "statusRequestMethod",
        "changeRequestMethod",
        {
            "key": "convertStatusRequestMethod",
            "type": "javascript"
        },
        {
            "key": "convertStatusChangeMethod",
            "type": "javascript"
        },
        "requestTimeout"

            ]
}
