descriptor.templateCss:
body {
	 background-color: #eee;
	 padding-top: 40px;
}
 h1 {
	 text-align: center;
	 font-size: 20px;
	 text-transform: uppercase;
	 letter-spacing: 1.2px;
}
 h2 {
	 text-align: center;
	 font-size: 14px;
	 text-transform: uppercase;
	 letter-spacing: 1.2px;
}
 hr {
	 margin: 0 auto;
	 margin-top: 50px;
	 margin-bottom: 50px;
}
 footer {
	 text-align: center;
	 padding-bottom: 50px;
}
 .link {
	 text-align: center;
	 color: #278fb2;
}
 .checkbox {
	 width: 280px;
	 margin: 0 auto;
	 top: 50%;
     transform:translate(-50%, -50%);

    position: absolute;
  left: 55%;
}
 .checkbox__container {
	 display: block;
	 position: relative;
	 height: 100px;
}
 .checkbox__toggle {
	 display: none;
}
 .checkbox__toggle:checked ~ .checkbox__path .path--top {
	 stroke-dashoffset: 0px;
	 stroke: #0cb018;
}
 .checkbox__toggle:checked ~ .checkbox__path .path--bottom {
	 stroke-dasharray: 14px 360px;
	 stroke-dashoffset: 0px;
	 stroke: #0cb018;
}
 .checkbox__checker {
	 position: absolute;
	 top: 13px;
	 left: 12px;
	 z-index: 1;
	 display: block;
	 width: 73px;
	 height: 73px;
	 background-color: #fff;
	 border-radius: 50%;
	 z-index: 9;
}
 .checkbox__bg, .checkbox__path {
	 position: absolute;
	 top: 0;
	 left: 0;
	 width: 100%;
	 height: 100%;
}
 .checkbox__bg .shape-bg {
	 fill: #aaa;
}
 .checkbox__path {
	 z-index: 9;
}
 .checkbox__path .shape-bg {
	 fill: #fff;
	 stroke-width: 1px;
	 stroke: #aaa;
}
 .checkbox__path .path {
	 stroke-width: 4px;
	 stroke: #bf1e1e;
	 fill: none;
	 stroke-linecap: round;
	 transition: 1s;
}
 .checkbox__path .path--top {
	 stroke-dasharray: 33px 360px;
	 stroke-dashoffset: -336px;
}
 .checkbox__path .path--bottom {
	 stroke-dasharray: 33px 360px;
	 stroke-dashoffset: -324px;
}
 .checkbox #checkbox__circle {
	 z-index: 9;
	 position: absolute;
	 top: 0;
	 left: 0;
	 width: 100%;
	 height: 100%;
	 fill: #fff;
}
 .anim {
	 transition-timing-function: cubic-bezier(0.385, -0.6, 0.685, 1.59);
	 transition-duration: 0.2s;
}
 .anim--go-right {
	 left: calc(100% - 120px);
}
 .anim--speed {
	 animation-duration: 0.5s;
	 animation-name: speedSmall;
}
 @keyframes speed {
	 0% {
		 transform: scaleY(0.6);
	}
	 30% {
		 transform: scaleY(2.3);
	}
	 60% {
		 transform: scaleY(2.3);
	}
	 100% {
		 transform: scaleY(0.6);
	}
}
 @keyframes speedSmall {
	 0% {
		 transform: scale(1, 1);
	}
	 25% {
		 transform: scale(0.7, 0.7);
	}
	 35% {
		 transform: scale(0.7, 0.7);
	}
	 50% {
		 transform: scale(0.7, 0.3);
	}
	 65% {
		 transform: scale(0.7, 0.7);
	}
	 75% {
		 transform: scale(0.7, 0.7);
	}
	 100% {
		 transform: scale(1, 1);
	}
}

descriptor.controllerScript:
var namespace;
var cssParser = new cssjs();

self.onInit = function() {

    var settings = self.ctx.settings;
    //Set Value Mathod
    var convertStatusChangeMethod = settings.convertStatusChangeMethod || "return value;";

    var convertStatus = new Function("value", convertStatusChangeMethod);

    //Get Value Method
    var convertGetValueMethod = settings.convertStatusRequestMethod || "return data ? true : false;";

    var convertGetValue = new Function("data", convertGetValueMethod);


var toggler = $('.checkbox__toggle', self.ctx.$container);
var circle = $('.checkbox__checker', self.ctx.$container);

var time1 = 150;
var time2 = 800;
toggler.click(function(){
    var paramsBody = convertStatus(false);

    checkCheckbox($(this).is(":checked"))
    if ($(this).is(":checked"))
        paramsBody = convertStatus(true);
      var method = settings.changeRequestMethod || 'setValue';

            console.log(method);
            console.log($(this).is(":checked"))

            self.ctx.controlApi.sendOneWayCommand(method, 
                                        paramsBody, settings.requestTimeout)
                        /*.subscribe(
                            function success(responseBody) {

                            }
                        );*/


})

function checkCheckbox(checked)
{

    //toggler.checked = true;

    circle.removeClass('anim--speed');
  if(checked){
    setTimeout(function(){
      circle.addClass('anim--speed');
    },10);
    setTimeout(function(){
      circle.addClass('anim--go-right');
    },time1);
    //toggler.is(":checked") = true;

  }
  else{
      toggler.checked = false;
    setTimeout(function(){
      circle.addClass('anim--speed');
    },10);
    setTimeout(function(){
      circle.removeClass('anim--go-right');
    },time1);
    //toggler.is(":checked") = false;
  }
}




   function requestStatus() {

        method = settings.statusRequestMethod;
        paramsBody = "";
        self.ctx.controlApi.sendTwoWayCommand(method, 
                            paramsBody, 
                            settings.requestTimeout)
            .subscribe(
                function success(responseBody) {

                       let value = (convertGetValue(responseBody)); 

                       if (value)
                       {
                       toggler.click();


                       }
                        else
                        {


                        }
                    }
                    );
                }

if ((settings.retrieveValueMethod == "Subscribe for attribute") || (settings.retrieveValueMethod == "Subscribe for timeseries"))
{
var type = 'timeseries'
if (settings.retrieveValueMethod == "Subscribe for attribute")
    type = 'attribute'
datasources = [
        {  // datasource
           type: 'entity',// type of the datasource. Can be "function" or "entity"
           name: 'name', // name of the datasource (in case of "entity" usually Entity name)
           aliasName: self.ctx.defaultSubscription.targetDeviceName, // name of the alias used to resolve this particular datasource Entity
           entityName: self.ctx.defaultSubscription.targetDeviceName, // name of the Entity used as datasource
           entityType: 'DEVICE', // datasource Entity type (for ex. "DEVICE", "ASSET", "TENANT", etc.)
           entityId: self.ctx.defaultSubscription.targetDeviceId, // entity identificator presented as string uuid. 
           dataKeys: [ //  array of keys (Array<DataKey>) (attributes or timeseries) of the entity used to fetch data 
               { // dataKey
                    name: settings.updateValueKey, // the name of the particular entity attribute/timeseries 
                    type: type, // type of the dataKey. Can be "timeseries", "attribute" or "function" 
                    label: settings.updateValueKey, // label of the dataKey. Used as display value (for ex. in the widget legend section) 
                    color: '#ffffff', // color of the key. Can be used by widget to set color of the key data (for ex. lines in line chart or segments in the pie chart).  
                    funcBody: "", // only applicable for datasource with type "function" and "function" key type. Defines body of the function to generate simulated data.
                    settings: {} // dataKey specific settings with structure according to the defined Data key settings json schema. See "Settings schema section".
               },
               //...
           ]
        },
        //...
    ]
  function subscribeForLabelPatternsSources(datasources) {
    const labelPatternsSourcesSubscriptionOptions = {
      datasources,
      useDashboardTimewindow: false,
      type: 'latest',
      callbacks: {
        onDataUpdated: (subscription) => {

          let data = subscription.data[0].data[0][1];

          let value = (convertGetValue(data)); 

                       if (value === true)
                       {
                           if (!toggler.is(":checked"))
                                toggler.click();

                       }
                        else
                        {
                            if (toggler.is(":checked"))
                            toggler.click();

                        }
        }
      }
    };

    self.ctx.subscriptionApi.createSubscription(labelPatternsSourcesSubscriptionOptions, true).subscribe(
      (subscription) => {
        //this.labelPatternsSourcesSubscription = subscription;
      }
    );
  }
subscribeForLabelPatternsSources(datasources);
}

    if (settings.retrieveValueMethod == 'Call RPC get value method')
    {
        requestStatus()
    }


self.onResize();

}


self.onResize = function() {
    if (self.ctx.width > self.ctx.height+150)
        $('.checkbox', self.ctx.$container)[0].style.zoom = self.ctx.height/180;
    else
    $('.checkbox', self.ctx.$container)[0].style.zoom = self.ctx.width/300;

    self.ctx.detectChanges();
}

self.onDestroy = function() {
}

descriptor.settingsSchema:
{
    "schema": {
        "type": "object",
        "title": "Settings",
        "properties": {
            "retrieveValueMethod":
            {
                "title": "Retrieve on/off value using method",
                            "type": "string",
                            "enum": [
                                "Don't retrieve",
                                "Call RPC get value method",
                                "Subscribe for attribute",
                                "Subscribe for timeseries"],
                            "default": "Call RPC get value method"
            },
            "updateValueKey": {
                "title": "Attribute/Timeseries value key (only when subscribe for attribute/timeseries method)",
                "type": "string",
                "default": "value"
            },
            "requestTimeout": {
                "title": "RPC request timeout",
                "type": "number",
                "default": 500
            },
            "changeRequestMethod": {
                "title": "RPC set value method",
                "type": "string",
                "default": "setValue"
            },
            "statusRequestMethod": {
                "title": "RPC get value method",
                "type": "string",
                "default": "getValue"
            },
            "convertStatusChangeMethod": {
                "title": "Convert value function, f(value), returns payload used by RPC set value method",
                "type": "string",
                "default": "return value;"
            },
            "convertStatusRequestMethod": {
                "title": "Parse value function, f(data), returns boolean",
                "type": "string",
                "default": "return data ? true : false;"
            } 

        },
        "required": [ 
                    "retrieveValueMethod",
                     "statusRequestMethod",
                     "changeRequestMethod",
                     "convertStatusRequestMethod",
                     "convertStatusChangeMethod",
                     "requestTimeout"]
    },
    "form": [
        "retrieveValueMethod",
        "updateValueKey",
        "statusRequestMethod",
        "changeRequestMethod",
        {
            "key": "convertStatusRequestMethod",
            "type": "javascript"
        },
        {
            "key": "convertStatusChangeMethod",
            "type": "javascript"
        },
        "requestTimeout"

            ]


}
