descriptor.templateCss:
@import url('https://fonts.googleapis.com/css?family=Open+Sans:300,400,600,700');
 * {
	 box-sizing: border-box;
	 -webkit-user-select: none;
	 -moz-user-select: none;
	 -ms-user-select: none;
	 user-select: none;
}
 html, body {
	 margin: 0;
	 background: #fdfdfd;
	 font-family: 'Open Sans', sans-serif;
}
 .center_radius, .thermostat, .thermostat .bar, .thermostat .center, .thermostat .center .small {
	 top: 50%;
	 left: 50%;
	 transform: translate(-50%, -50%);
	 border-radius: 50%;
}
 .thermostat {
	 position: absolute;
	 width: 400px;
	 height: 400px;
	 background: #6d697f;
	 box-shadow: inset 0px -6px 1px 2px rgba(0, 0, 0, 0.35), 0px 7px 40px 11px rgba(84, 81, 97, 0.40);
}
 .thermostat:hover .number {
	 opacity: 1;
}
 .thermostat .shadow {
	 position: absolute;
	 top: 50%;
	 left: 50%;
	 transform: translate(-50%, -50%) rotateZ(10deg);
	 width: 25px;
	 height: 86%;
	 text-align: center;
	 transition: 0.7s ease;
	 animation: shadow 1.4s ease-out both;
}
 .thermostat .shadow .shadow-cube {
	 position: absolute;
	 top: 0;
	 width: 25px;
	 height: 0px;
	 box-shadow: 0 0 45px 13px rgba(255, 158, 35, 0.5);
}
 .thermostat .number {
	 position: absolute;
	 top: 50%;
	 left: 50%;
	 transform: translate(-50%, -50%) rotateZ(10deg);
	 width: 40px;
	 height: 82%;
	 text-align: center;
	 transition: 0.7s ease;
	 opacity: 0;
}
 .thermostat .number .ext {
	 font-size: 16px;
	 color: white;
	 font-weight: 1000;
	 text-shadow: 1px 3px 3px #302e38;
}
 .thermostat .bar {
	 position: absolute;
	 width: 356px;
	 height: 356px;
}
 .thermostat .bar .inner_bar {
	 position: absolute;
	 top: 50%;
	 left: 50%;
	 width: 344px;
	 height: 344px;
	 margin-left: -172px;
	 margin-top: -172px;
	 border-radius: 100%;
	 background-color: #6d697f;
	 z-index: 4;
}
 .thermostat .bar .inner_bar:after {
	 content: '';
	 display: block;
	 position: absolute;
	 width: 0;
	 height: 0;
	 border-top: 70px solid transparent;
	 border-left: 70px solid transparent;
	 border-right: 70px solid transparent;
	 border-bottom: 150px solid #6d697f;
	 bottom: -7px;
	 left: 50%;
	 transform: translatex(-50%);
}
 .thermostat .bar .hold {
	 position: absolute;
	 width: 100%;
	 height: 100%;
	 clip: rect(0px, 356px, 356px, 178px);
	 border-radius: 100%;
	 background-color: #3a3749;
}
 .thermostat .bar .fill {
	 position: absolute;
	 width: 100%;
	 height: 100%;
	 border-radius: 100%;
	 clip: rect(0px, 178px, 356px, 0px);
}
 .thermostat .bar .fill1 {
	 background: -webkit-linear-gradient(top, #ff4900 20%, #ff4900 100%);
}
 .thermostat .bar .fill2 {
	 background: -webkit-linear-gradient(top, #ff4900 40%, #ff9e23 100%);
}
 .thermostat .bar .right {
	 z-index: 3;
	 -webkit-transform: rotate(180deg);
	 -moz-transform: rotate(180deg);
	 transform: rotate(180deg);
}
 .thermostat .bar .right .fill {
	 z-index: 3;
	 transform: rotate(180deg);
	 animation: right 1s linear both;
	 transition: transform 0.6s;
}
 .thermostat .bar .left .fill {
	 z-index: 1;
	 animation: left 0.3s linear both;
	 animation-delay: 1s;
	 transition: transform 0.6s;
}
 .thermostat .bar span {
	 width: 356px;
	 font-weight: 800;
	 position: absolute;
	 bottom: 0px;
	 text-align: center;
	 text-transform: uppercase;
	 font-size: 15px;
	 color: #2e2c3a;
	 z-index: 10;
}
 .thermostat .center {
	 position: absolute;
	 width: 260px;
	 height: 260px;
	 background: #e3e4ed;
	 animation: bound-in 0.6s ease forwards;
}
 .thermostat .center:hover .arrow {
	 opacity: 1;
	 transition: opacity 0.3s;
}
 .thermostat .center:hover .minus {
	 left: 10px;
	 transition: left 0.3s ease-out;
}
 .thermostat .center:hover .plus {
	 right: 45px;
	 transition: right 0.3s ease-out;
}
 .thermostat .center span i {
	 position: absolute;
	 color: #b9b6c8;
	 font-size: 40px;
	 font-weight: 500;
	 line-height: 260px;
}
 .thermostat .center .arrow {
	 opacity: 0;
	 transition: opacity 0.3s;
	 cursor: pointer;
}
 .thermostat .center .minus {
	 position: absolute;
	 animation: arrow-left 1s forwards;
}
 .thermostat .center .plus {
	 position: absolute;
	 animation: arrow-right 1s forwards;
}
 .thermostat .center .small {
	 position: absolute;
	 width: 150px;
	 height: 150px;
	 background: #f8f9fa;
	 text-align: center;
	 animation: bound-in-small 0.6s ease forwards;
}
 .thermostat .center .small .heat {
	 line-height: 150px;
	 font-size: 59px;
	 color: #57545f;
	 font-weight: 300;
}
 .thermostat .center .small .heat:after {
	 content: 'Â°';
	 display: block;
	 position: absolute;
	 font-size: 40px;
	 top: -20px;
	 right: 30px;
}
 @-webkit-keyframes shadow {
	 0% {
		 transform: translate(-50%, -50%) rotate(-190deg);
	}
	 100% {
		 transform: translate(-50%, -50%) rotate(10deg);
	}
}
 @-webkit-keyframes right {
	 0% {
		 transform: rotate(0deg);
	}
	 100% {
		 transform: rotate(180deg);
	}
}
 @-webkit-keyframes left {
	 0% {
		 transform: rotate(0deg);
	}
	 100% {
		 transform: rotate(10deg);
	}
}
 @-webkit-keyframes bound-in {
	 10% {
		 box-shadow: 0px 7px 30px 5px rgba(96, 93, 111, 0.25);
		 transform: translate(-50%, -50%) scale(0.8);
	}
	 80% {
		 transform: translate(-50%, -50%) scale(1.03);
	}
	 100% {
		 box-shadow: 0px 15px 35px 11px rgba(46, 44, 58, 0.60);
		 transform: translate(-50%, -50%) scale(1);
	}
}
 @-webkit-keyframes bound-in-small {
	 0% {
		 box-shadow: 0px 5px 10px 5px rgba(96, 93, 111, 0.19);
		 transform: translate(-50%, -50%) scale(0.8);
	}
	 80% {
		 transform: translate(-50%, -50%) scale(1.03);
	}
	 100% {
		 box-shadow: 0px 15px 35px 5px rgba(96, 93, 111, 0.30);
		 transform: translate(-50%, -50%) scale(1);
	}
}
 @-webkit-keyframes arrow-left {
	 0% {
		 left: 45px;
		 opacity: 0;
	}
	 100% {
		 left: 10px;
		 opacity: 1;
	}
}
 @-webkit-keyframes arrow-right {
	 0% {
		 right: 75px;
		 opacity: 0;
	}
	 100% {
		 right: 45px;
		 opacity: 1;
	}
}

descriptor.controllerScript:
var gradi = 55;
self.onInit = function() {
var settings = self.ctx.settings;

var max = settings.maxValue;
var min = settings.minValue;
var units = 320 / (max - min);
var center_value = (min + max) / 2;
var start_degree = -160;
const start_degree_delta = 20;
const fill2_start_value = 180;
var move_unit = 1;
if (settings.stepWidth == '0.1')
    move_unit = 0.1;

requestStatus();

function updateGr() {
  $('.heat', self.ctx.$container).text('' + gradi);
  $('.ext', self.ctx.$container).text('' + gradi);
  var v = start_degree + (gradi - min) * units;
  console.log(v);
  $('.number', self.ctx.$container).css('transform', `translate(-50%, -50%) rotate(${v}deg)`);
  $('.fill', self.ctx.$container).css('animation', 'none');
  $('.shadow', self.ctx.$container).css('animation', 'none');
}

const getFixedValue = (value) => {
  console.log(value);
  return +value.toFixed(1);
};

const minux_action = () => {
  if (gradi > min) {
    gradi = getFixedValue(gradi - move_unit);
    updateGr();
    const low_value = center_value - 1;

    if (gradi >= center_value) {
      $('.fill1', self.ctx.$container)
        .css('transform', `rotate(${(gradi - center_value) * units}deg)`)
        .css('transition-delay', '0s');
    } else if (gradi == low_value) {
      $('.fill2', self.ctx.$container)
        .css(
          'transform',
          `rotate(${(gradi - min) * units + start_degree_delta}deg)`
        )
        .css('transition-delay', '0.5s');
    } else {
      $('.fill2', self.ctx.$container)
        .css(
          'transform',
          `rotate(${(gradi - min) * units + start_degree_delta}deg)`
        )
        .css('transition-delay', '0s');
        $('.fill1', self.ctx.$container)
        .css(
          'transform',
          `rotate(${0}deg)`
        )
        .css('transition-delay', '0s');

    }
    sendValue();
  }

};
const plus_action = () => {
  const top_value = center_value + 1;
  if (gradi < max) {
    gradi = getFixedValue(gradi + move_unit);
    updateGr();
    if (gradi > top_value) {
      $('.fill1', self.ctx.$container)
        .css('transform', `rotate(${(gradi - center_value) * units}deg)`)
        .css('transition-delay', '0s');
    } else if (gradi == top_value) {
      $('.fill1', self.ctx.$container)
        .css('transform', `rotate(${(gradi - center_value) * units}deg)`)
        .css('transition-delay', '0.5s');
    } else {
      $('.fill2', self.ctx.$container)
        .css(
          'transform',
          `rotate(${(gradi - min) * units + start_degree_delta}deg)`
        )
        .css('transition-delay', '0s');
    }
    sendValue();
  }

};
const init = () => {
  minux_action();
  plus_action();
  updateGr();
};

$('.minus', self.ctx.$container).mousedown(minux_action);
$('.plus', self.ctx.$container).mousedown(plus_action);

init();

function requestStatus() {
    method = settings.statusRequestMethod;
    timeout = settings.requestTimeout;
    paramsBody = "";
    self.ctx.controlApi.sendTwoWayCommand(method, 
                        paramsBody, 
                        timeout)
        .subscribe(
            function success(responseBody) {
                   gradi = parseFloat(responseBody);   
                   if (gradi < min)
                        gradi = min;
                 if (gradi > max)
                    gradi = max;
                   updateGr();
                   init();
                }
                );
}






}

    sendValue = function() {
        var settings = self.ctx.settings;
        var method = settings.changeRequestMethod;

        var paramsBody = gradi;



        self.ctx.controlApi.sendTwoWayCommand(method, 
                                    paramsBody, settings.requestTimeout)
                    .subscribe(
                        function success(responseBody) {

                        }
                    );
}


self.onResize = function() {
    if (self.ctx.height < self.ctx.width)
 $('.thermostat', self.ctx.$container)[0].style.zoom =  1*(self.ctx.height / 410);
    else
     $('.thermostat', self.ctx.$container)[0].style.zoom =  1*(self.ctx.width / 410);
}

self.onDestroy = function() {
}

descriptor.settingsSchema:
{
    "schema": {
        "type": "object",
        "title": "Settings",
        "properties": {
            "minValue": {
                "title": "Minimum Value",
                "type": "number",
                "default": 0
            },
            "maxValue": {
                "title": "Maximum Value",
                "type": "number",
                "default": 100
            },
            "stepWidth": {
                "title": "Step Width",
                "type": "string",
                "enum": [
                    "0.1",
                    "1"],
                "default": "1"
            },

            "requestTimeout": {
                "title": "RPC request timeout",
                "type": "number",
                "default": 500
            },
            "statusRequestMethod": {
                "title": "Status request Method",
                "type": "string",
                "default": "getValue"
            },
            "changeRequestMethod": {
                "title": "Change request Method",
                "type": "string",
                "default": "setValue"
            },
        "required": [
                     "requestTimeout",
                     "statusRequestMethod",
                      "changeRequestMethod"
                     ]
        }
    },
    "form": [
        "minValue",
        "maxValue",
        "stepWidth",
        "requestTimeout",
    "statusRequestMethod",
     "changeRequestMethod"
    ]

}
