descriptor.templateCss:
#container {
	 background: #b197fc;
	 background: linear-gradient(to right, #d0bfff, #9775fa);
	 color: #7950f2;
}
 .thermostat {
	 position: absolute;
	 left: 50%;
	 top: 50%;
	 transform: translateX(-50%) translateY(-50%);
}
 .thermostat .temperature {
	 font-size: 100px;
	 font-weight: 600;
	 text-align: center;
}
 .thermostat .temperature span::selection {
	 background: transparent;
}
 .thermostat .control {
	 font-size: 32px;
	 text-align: center;
}
 .thermostat .control i:hover {
	 cursor: pointer;
}

descriptor.controllerScript:
var namespace;
var cssParser = new cssjs();

var number = 20;

self.onInit = function() {
    var settings = self.ctx.settings;
    var move_unit = 1;
if (settings.stepWidth == '0.1')
    move_unit = 0.1;

requestStatus();

    requestStatus()


    const temperature = document.querySelector(".degrees")
const up = document.querySelector(".up i")
const down = document.querySelector(".down i")



temperature.innerHTML = number

function hotter() {
  up.style.opacity = "1"
  down.style.opacity = "1"

  if (number < settings.maxValue) {
    number = number + move_unit;
    temperature.innerHTML = number.toFixed(1);
  }

  if (number === settings.maxValue) {
    up.style.opacity = "0.5"
  }

  sendValue();

}

function colder() {
  up.style.opacity = "1"
  down.style.opacity = "1"

  if (number > settings.minValue) {
    number = number - move_unit;
    temperature.innerHTML = number.toFixed(1);
  }

  if (number === settings.minValue) {
    down.style.opacity = "0.5"
  }

  sendValue();





}

up.addEventListener("click", hotter)
down.addEventListener("click", colder)

    sendValue = function() {
        var settings = self.ctx.settings;
        var method = settings.changeRequestMethod;

        var paramsBody = number.toFixed(1);



        self.ctx.controlApi.sendTwoWayCommand(method, 
                                    paramsBody, settings.requestTimeout)
                    .subscribe(
                        function success(responseBody) {

                        }
                    );
}


function requestStatus() {
    method = settings.statusRequestMethod;
    timeout = settings.requestTimeout;
    paramsBody = "";
    self.ctx.controlApi.sendTwoWayCommand(method, 
                        paramsBody, 
                        timeout)
        .subscribe(
            function success(responseBody) {
                   number = parseFloat(responseBody);   
                   if (number < settings.min)
                        number = settings.min;
                 if (number > settings.max)
                    number = settings.max;
                   temperature.innerHTML = number;
                }
                );
}


}



self.onResize = function() {
}

self.onDestroy = function() {
}

descriptor.settingsSchema:
{
    "schema": {
        "type": "object",
        "title": "Settings",
        "properties": {
            "minValue": {
                "title": "Minimum Value",
                "type": "number",
                "default": 0
            },
            "maxValue": {
                "title": "Maximum Value",
                "type": "number",
                "default": 100
            },
            "stepWidth": {
                "title": "Step Width",
                "type": "string",
                "enum": [
                    "0.1",
                    "1"],
                "default": "1"
            },

            "requestTimeout": {
                "title": "RPC request timeout",
                "type": "number",
                "default": 500
            },
            "statusRequestMethod": {
                "title": "Status request Method",
                "type": "string",
                "default": "getValue"
            },
            "changeRequestMethod": {
                "title": "Change request Method",
                "type": "string",
                "default": "setValue"
            },
        "required": [
                     "requestTimeout",
                     "statusRequestMethod",
                      "changeRequestMethod"
                     ]
        }
    },
    "form": [
        "minValue",
        "maxValue",
        "stepWidth",
        "requestTimeout",
    "statusRequestMethod",
     "changeRequestMethod"
    ]

}
