descriptor.templateCss:
.switch input {
    /* First, we make it as wide as the container */
    position: absolute;
    width: 100%;
    height: 100%;
    /* Then, we put it on top of everything else */
    z-index: 100;
    /* Last, we make it invisible */
    opacity: 0;
    /* This one is just for ergonomy */
    cursor: pointer;
}

.switch {
    width: 100px;
    height: 100px;
   
    
    top: 50%;
     transform: translate(-50%, -50%);
    position: absolute;
  left: 50%;
}

.switch label {
    display: block;
    width: 100%;
    height: 100%;
    position: relative;
    border-radius: 50%;
    background: #eaeaea;
    box-shadow: 
        0 3px 5px rgba(0,0,0,0.25),
        inset 0 1px 0 rgba(255,255,255,0.3),
        inset 0 -5px 5px rgba(100,100,100,0.1),
        inset 0 5px 5px rgba(255,255,255,0.3);
}

.switch label:after {
    content: "";
    position: absolute;
    z-index: -1;
    top: -8%; 
    right: -8%; 
    bottom: -8%; 
    left: -8%;
    border-radius: inherit;
    background: #ddd; /* Fallback */
    background: linear-gradient(#ccc, #fff);
    box-shadow: 
        inset 0 2px 1px rgba(0,0,0,0.15),
        0 2px 5px rgba(200,200,200,0.1);
}

.switch label:before {
    content: "";
    position: absolute;
    width: 20%;
    height: 20%; 
    left: 40%;
    top: 40%;
    border-radius: inherit;
    background: #969696; /* Fallback */
    background: radial-gradient(40% 35%, #ccc, #969696 60%);
    box-shadow:
        inset 0 2px 4px 1px rgba(0,0,0,0.3),
        0 1px 0 rgba(255,255,255,1),
        inset 0 1px 0 white;
}

.switch input:checked ~ label { /* Button */
	background: #e5e5e5; /* Fallback */
    background: linear-gradient(#dedede, #fdfdfd);
}

.switch input:checked ~ label:before { /* LED */
    background: #25d025; /* Fallback */
    background: radial-gradient(40% 35%, #5aef5a, #25d025 60%);
    box-shadow:
        inset 0 3px 5px 1px rgba(0,0,0,0.1),
        0 1px 0 rgba(255,255,255,0.4),
        0 0 10px 2px rgba(0, 210, 0, 0.5);
}

descriptor.controllerScript:
var namespace;
var cssParser = new cssjs();

self.onInit = function() {
    var settings = self.ctx.settings;

    //Set Value Mathod
    var convertStatusChangeMethod = settings.convertStatusChangeMethod || "return value;";
    var convertStatus = new Function("value", convertStatusChangeMethod);

    //Get Value Method
    var convertGetValueMethod = settings.convertStatusRequestMethod || "return data ? true : false;";

    var convertGetValue = new Function("data", convertGetValueMethod);


    function requestStatus() {
        method = settings.statusRequestMethod;
        paramsBody = "";
        self.ctx.controlApi.sendTwoWayCommand(method, 
                            paramsBody, 
                            settings.requestTimeout)
            .subscribe(
                function success(responseBody) {
                       let value = (convertGetValue(responseBody));   
                       console.log (value);
                       if (value == true)
                       {

                       $('#switch', self.ctx.$container).click();

                       }
                        else
                        {



                        }
                    }
                    );
                }




    requestStatus()


    $('#switch', self.ctx.$container).click(function(e){
  var method = settings.changeRequestMethod || 'setValue';
        let checked = $('#switch', self.ctx.$container)[0].checked;
        console.log(checked);
        var paramsBody = convertStatus(checked);
        console.log(method);
        console.log(paramsBody);

        self.ctx.controlApi.sendOneWayCommand(method, 
                                    paramsBody, settings.requestTimeout)
                    /*.subscribe(
                        function success(responseBody) {

                        }
                    );*/
});
    self.onResize();

}



self.onResize = function() {
    if (self.ctx.width > self.ctx.height)
        $('.switch', self.ctx.$container)[0].style.zoom = self.ctx.height / 150;
    else
        $('.switch', self.ctx.$container)[0].style.zoom = self.ctx.width / 150;
}

self.onDestroy = function() {
}

descriptor.settingsSchema:
{
    "schema": {
        "type": "object",
        "title": "Settings",
        "properties": {
            "requestTimeout": {
                "title": "RPC request timeout",
                "type": "number",
                "default": 500
            },
            "changeRequestMethod": {
                "title": "RPC set value method",
                "type": "string",
                "default": "setValue"
            },
            "statusRequestMethod": {
                "title": "RPC get value method",
                "type": "string",
                "default": "getValue"
            },
            "convertStatusChangeMethod": {
                "title": "Convert value function, f(value), returns payload used by RPC set value method",
                "type": "string",
                "default": "return value;"
            } ,
            "convertStatusRequestMethod": {
                "title": "Parse value function, f(data), returns boolean",
                "type": "string",
                "default": "return data ? true : false;"
            } 
        },
        "required": [ 
                     "requestTimeout",
                     "changeRequestMethod",
                     "statusRequestMethod",
                     "convertStatusChangeMethod",
                     "convertStatusRequestMethod"
                    ]
    },
    "form": [
        "statusRequestMethod",
        "changeRequestMethod",
        {
            "key": "convertStatusRequestMethod",
            "type": "javascript"
        },
        {
            "key": "convertStatusChangeMethod",
            "type": "javascript"
        },
        "requestTimeout"
            ]
}
